import React, { useState } from "react";
import YouTube from "react-youtube";
import "./App.css";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [showScrollPage, setShowScrollPage] = useState(false);

  const opts = {
    playerVars: {
      autoplay: 1, // Otomatis memainkan
      controls: 0, // Tidak menampilkan kontrol video
      modestbranding: 1,
      rel: 0,
    },
  };

  const handleScrollToBegin = () => {
    setShowScrollPage(true);
  };

  return (
    <div className="app-container">
      {!isOpen && !showScrollPage && (
        <>
          <div className="right-section">
            <div className="announcement-text">
              <b>WEDDING ANNOUNCEMENT</b>
            </div>
            <h1 className="title">TIFFANY & JARED</h1>
            <p className="hashtag">#TImetoshaRE</p>
            <button className="open-button" onClick={() => setIsOpen(true)}>
              Open
            </button>
            <div className="scroll-to-begin" onClick={handleScrollToBegin}>
              Scroll to Begin
            </div>
          </div>
        </>
      )}

      {isOpen && (
        <div className="music-container">
          <YouTube videoId="u03oQwQVR9E" opts={opts} onEnd={() => setIsOpen(false)} />
        </div>
      )}

      {showScrollPage && (
        <div className="scroll-page">
          <div className="scroll-content">
            <h2>DEAR MR-MRS-MS,</h2>
            <p>
              Family & Friends <br />
              Welcome to Tiffany & Jaredâ€™s Wedding Website <br />
              Together with joyful hearts and the grace of God, we joyfully
              announce the upcoming of our marriage.
            </p>
          </div>
          <div className="slider">
            <div className="slider-image">
              <img src="./assets/images/left-image.jpg" alt="Slide 1" />
            </div>
            <div className="slider-image">
              <img src="./assets/images/right-image.jpg" alt="Slide 2" />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
