import React, { useState, useRef } from "react";
import YouTube from "react-youtube";
import "./App.css";
import image1 from "./assets/images/image1.jpg";
import image2 from "./assets/images/image2.jpg";
import image3 from "./assets/images/image3.jpg";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isWelcomeOpen, setIsWelcomeOpen] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const rightSectionRef = useRef(null);

  // Array gambar slider
  const images = [image1, image2, image3];

  // Handle next/previous image
  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Open the "welcome" message
  const handleOpenClick = () => {
    setIsOpen(true);
  };

  const opts = {
    height: "0",
    width: "0",
    playerVars: {
      autoplay: 1,
      controls: 0,
      modestbranding: 1,
      rel: 0,
      showinfo: 0,
    },
  };

  return (
    <div className="app-container">
      {/* Left Section */}
      <div className="left-section">
        <div className="announcement-text"><b>WEDDING ANNOUNCEMENT</b></div>
        <h1 className="title">TIFFANY & JARED</h1>
        <p className="quote">
          "Aku ingin mencintaimu dengan sederhana; dengan kata yang tak sempat
          diucapkan kayu kepada api yang menjadikannya abu."
        </p>
      </div>

      {/* Right Section */}
      {!isOpen && (
        <div className="right-section" ref={rightSectionRef}>
          <div className="announcement-text"><b>WEDDING ANNOUNCEMENT</b></div>
          <h1 className="title">TIFFANY & JARED</h1>
          <p className="hashtag">#TImetoshaRE</p>
          <button className="open-button" onClick={handleOpenClick}>
            Open
          </button>
        </div>
      )}

      {/* Slider Section */}
      {isOpen && (
        <div className="slider-container" ref={rightSectionRef}>
          <YouTube videoId="u03oQwQVR9E" opts={opts} />
          <div className="slider">
            <button
              className="slider-button left"
              onClick={handlePrevImage}
              aria-label="Previous image"
            >
              ❮
            </button>
            <img
              src={images[currentImageIndex]}
              alt="Wedding"
              className="slider-image"
            />
            <button
              className="slider-button right"
              onClick={handleNextImage}
              aria-label="Next image"
            >
              ❯
            </button>
          </div>
          <div className="welcome-text">
            <p>DEAR MR-MRS-MS, Family & Friends</p>
            <p>
              Welcome to Tiffany & Jared’s Wedding Website <br />
              Together with joyful hearts and the grace of God, we joyfully
              announce the upcoming of our marriage.
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
