import React, { useState, useEffect, useRef } from "react";
import YouTube from "react-youtube"; // Import react-youtube component
import "./App.css";
import leftImage from "./assets/images/left-image.jpg";
import rightImage from "./assets/images/right-image.jpg";
import image1 from "./assets/images/image1.jpg"; // Example image 1
import image2 from "./assets/images/image2.jpg"; // Example image 2
import image3 from "./assets/images/image3.jpg"; // Example image 3

const [currentImageIndex, setCurrentImageIndex] = useState(0); // State untuk gambar aktif
const images = [image1, image2, image3]; // Array gambar

const handlePrevImage = () => {
  setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
};

const handleNextImage = () => {
  setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
};


const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isWelcomeOpen, setIsWelcomeOpen] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0); // State for active image
  const images = [image1, image2, image3]; // Array of images
  const rightSectionRef = useRef(null);
  const playerRef = useRef(null); // Ref for YouTube player

  const handleOpenClick = () => {
    setIsOpen(true);
    if (playerRef.current) {
      playerRef.current.internalPlayer.playVideo(); // Play the YouTube audio
    }
  };

  const opts = {
    height: "0", // Hide the video player height
    width: "0", // Hide the video player width
    playerVars: {
      autoplay: 1, // Autoplay the video
      controls: 0, // Hide player controls
      modestbranding: 1, // Hide YouTube logo
      rel: 0, // Disable related videos
      showinfo: 0, // Hide video info
    },
  };

  const handlePrevImage = () => {
    setCurrentImageIndex(
      (prevIndex) => (prevIndex - 1 + images.length) % images.length
    );
  };

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="app-container">
      <div className="left-section">
        <div className="announcement-text">
          <b>WEDDING ANNOUNCEMENT</b>
        </div>
        <h1 className="title">TIFFANY & JARED</h1>
        <p className="quote">
          "Aku ingin mencintaimu dengan sederhana; dengan kata yang tak sempat
          diucapkan kayu kepada api yang menjadikannya abu. Aku ingin
          mencintaimu dengan sederhana; dengan isyarat yang tak sempat
          disampaikan awan kepada hujan yang menjadikannya tiada." <br />—
          Sapardi Djoko Damono
        </p>
      </div>

      {!isOpen && (
        <div className="right-section" ref={rightSectionRef}>
          <div className="announcement-text">
            <b>WEDDING ANNOUNCEMENT</b>
          </div>
          <h1 className="title">TIFFANY & JARED</h1>
          <p className="hashtag">#TImetoshaRE</p>
          <button className="open-button" onClick={handleOpenClick}>
            Open
          </button>
        </div>
      )}

      {isOpen && !isWelcomeOpen && (
        <div className="music-container">
          <YouTube videoId="u03oQwQVR9E" opts={opts} ref={playerRef} />
          <div className="slideshow">
            <img
              src={images[currentImageIndex]}
              alt="Slideshow"
              className="slideshow-image"
            />
            <div className="overlay-text">
              <div className="announcement-text">WEDDING ANNOUNCEMENT</div>
              <h1 className="title">TIFFANY & JARED</h1>
              <p className="hashtag">#TImetoshaRE</p>
            </div>
          </div>
          <div
            className="scroll-to-begin"
            onClick={() => setIsWelcomeOpen(true)}
          >
            Scroll to Begin
          </div>
        </div>
      )}

{isWelcomeOpen && (
  <div className="welcome-message">
    <p className="welcome-text">
      DEAR MR-MRS-MS,<br />
      Family & Friends<br />
      Welcome to<br />
      Tiffany & Jared’s Wedding Website<br />
      Together with joyful hearts and the grace of God, we joyfully announce the upcoming of our marriage.
    </p>
    <div className="slider-container">
      <button className="slider-button" onClick={handlePrevImage}>
        ❮
      </button>
      <img
        src={images[currentImageIndex]}
        alt="Wedding"
        className="slider-image"
      />
      <button className="slider-button" onClick={handleNextImage}>
        ❯
      </button>
    </div>
  </div>
)}

    </div>
  );
};

export default App;
