import React, { useState, useEffect, useRef } from "react";
import YouTube from "react-youtube";
import "./App.css";
import leftImage from "./assets/images/left-image.jpg";
import rightImage from "./assets/images/right-image.jpg";
import image1 from "./assets/images/image1.jpg";
import image2 from "./assets/images/image2.jpg";
import image3 from "./assets/images/image3.jpg";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isWelcomeOpen, setIsWelcomeOpen] = useState(false);
  const [currentImage, setCurrentImage] = useState(image1);
  const [containerSize, setContainerSize] = useState({ width: 0, height: 0 });
  const rightSectionRef = useRef(null);
  const playerRef = useRef(null);
  const welcomeSectionRef = useRef(null);

  useEffect(() => {
    if (rightSectionRef.current) {
      const { offsetWidth, offsetHeight } = rightSectionRef.current;
      setContainerSize({ width: offsetWidth, height: offsetHeight });
    }
  }, [isOpen]);

  useEffect(() => {
    let interval;
    if (isOpen) {
      interval = setInterval(() => {
        setCurrentImage((prevImage) => {
          if (prevImage === image1) return image2;
          if (prevImage === image2) return image3;
          return image1;
        });
      }, 3000);
    } else {
      setCurrentImage(image1);
    }
    return () => clearInterval(interval);
  }, [isOpen]);

  const handleOpenClick = () => {
    setIsOpen(true);
    if (playerRef.current) {
      playerRef.current.internalPlayer.playVideo();
    }
  };

  const handleScrollClick = () => {
    if (playerRef.current) {
      playerRef.current.internalPlayer.playVideo(); // Pastikan musik tetap berjalan
    }

    setIsWelcomeOpen(true);

    // Scroll ke bawah halaman secara mulus
    setTimeout(() => {
      if (welcomeSectionRef.current) {
        welcomeSectionRef.current.scrollIntoView({ behavior: "smooth" });
      }
    }, 500); // Delay agar memastikan animasi smooth tidak terganggu
  };



  const opts = {
    height: "0",
    width: "0",
    playerVars: {
      autoplay: 1,
      controls: 0,
      modestbranding: 1,
      rel: 0,
      loop: 1,
      playlist: "u03oQwQVR9E", // Loop video
    },
  };

  return (
    <div className="app-container">
      <div className="left-section">
        <div className="announcement-text"><b>WEDDING ANNOUNCEMENT</b></div>
        <h1 className="title">TIFFANY & JARED</h1>
        <p className="quote">
          "Aku ingin mencintaimu dengan sederhana..." <br />— Sapardi Djoko Damono
        </p>
      </div>

      {!isOpen && (
        <div className="right-section" ref={rightSectionRef}>
          <div className="announcement-text"><b>WEDDING ANNOUNCEMENT</b></div>
          <h1 className="title">TIFFANY & JARED</h1>
          <p className="hashtag">#TImetoshaRE</p>
          <button className="open-button" onClick={handleOpenClick}>Open</button>
        </div>
      )}

      {isOpen && (
        <div className="music-container" style={{ width: containerSize.width, height: containerSize.height }}>
          <YouTube
            videoId="u03oQwQVR9E"
            opts={opts}
            ref={playerRef}
          />
          <div className="slideshow" style={{ width: containerSize.width, height: containerSize.height }}>
            <img src={currentImage} alt="Slideshow" className="slideshow-image" />
            <div className="overlay-text">
              <div className="announcement-text">WEDDING ANNOUNCEMENT</div>
              <h1 className="title">TIFFANY & JARED</h1>
              <p className="hashtag">#TImetoshaRE</p>
            </div>
          </div>
          {!isWelcomeOpen && (
            <div className="scroll-to-begin" onClick={handleScrollClick}>
              Scroll to Begin
            </div>
          )}
        </div>
      )}

      {isWelcomeOpen && (
        <div className="welcome-message" ref={welcomeSectionRef}>
          <p className="welcome-text">
            <b>DEAR MR-MRS-MS,</b><br />
            Family & Friends<br />
            Welcome to<br />
            Tiffany & Jared’s Wedding Website<br /><br />
            <i>Together with joyful hearts and the grace of God...</i>
          </p>
          <div className="slideshow">
            <img src={currentImage} alt="Slideshow" className="slideshow-image" />
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
