import React, { useState, useEffect, useRef } from "react";
import YouTube from "react-youtube"; // Import react-youtube component
import "./App.css";
import image1 from "./assets/images/image1.jpg"; // Example image 1
import image2 from "./assets/images/image2.jpg"; // Example image 2
import image3 from "./assets/images/image3.jpg"; // Example image 3

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isWelcomeOpen, setIsWelcomeOpen] = useState(false); // New state
  const [currentImage, setCurrentImage] = useState(image1);
  const playerRef = useRef(null); // Ref for YouTube player

  // Change image every 3 seconds
  useEffect(() => {
    let interval;
    if (isOpen) {
      interval = setInterval(() => {
        setCurrentImage((prevImage) => {
          if (prevImage === image1) return image2;
          if (prevImage === image2) return image3;
          return image1;
        });
      }, 3000); // 3 seconds
    }

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [isOpen]);

  // Handle the play audio functionality when Open button is clicked
  const handleOpenClick = () => {
    setIsOpen(true);
    if (playerRef.current) {
      playerRef.current.internalPlayer.playVideo(); // Play the YouTube audio
    }
  };

  const opts = {
    height: "0", // Hide the video player height
    width: "0",  // Hide the video player width
    playerVars: {
      autoplay: 1,   // Autoplay the video
      controls: 0,   // Hide player controls
      modestbranding: 1, // Hide YouTube logo
      rel: 0,        // Disable related videos
      showinfo: 0,   // Hide video info
    },
  };

  return (
    <div className="app-container">
      {!isOpen ? (
        <div className="welcome-section">
          <h1 className="title">TIFFANY & JARED</h1>
          <p className="quote">"Aku ingin mencintaimu dengan sederhana..."</p>
          <button className="open-button" onClick={handleOpenClick}>
            Open
          </button>
        </div>
      ) : !isWelcomeOpen ? (
        <div className="slideshow-section">
          <YouTube
            videoId="u03oQwQVR9E" // Your YouTube video ID
            opts={opts}
            ref={playerRef}
          />
          <div className="slideshow">
            <img src={currentImage} alt="Slideshow" className="slideshow-image" />
          </div>
          <div className="scroll-to-begin" onClick={() => setIsWelcomeOpen(true)}>
            Scroll to Begin
          </div>
        </div>
      ) : (
        <div className="welcome-message">
          <h2>Welcome to Tiffany & Jaredâ€™s Wedding</h2>
          <div className="slideshow">
            <img src={currentImage} alt="Slideshow" className="slideshow-image" />
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
