import React, { useState, useRef, useEffect } from "react";
import YouTube from "react-youtube";
import "./App.css";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [showTextSection, setShowTextSection] = useState(false);
  const playerRef = useRef(null);

  const opts = {
    playerVars: {
      autoplay: 1, // Otomatis memainkan
      controls: 0, // Tidak menampilkan kontrol video
      modestbranding: 1,
      rel: 0,
    },
  };

  const handleScrollToTextSection = () => {
    setShowTextSection(true);
  };

  return (
    <div className="app-container">
      {!isOpen && !showTextSection && (
        <div className="right-section">
          <div className="announcement-text">
            <b>WEDDING ANNOUNCEMENT</b>
          </div>
          <h1 className="title">TIFFANY & JARED</h1>
          <p className="hashtag">#TImetoshaRE</p>
          <button className="open-button" onClick={() => setIsOpen(true)}>
            Open
          </button>
        </div>
      )}

      {isOpen && (
        <div className="music-container">
          <YouTube
            videoId="u03oQwQVR9E"
            opts={opts}
            ref={playerRef}
          />
          <div className="slideshow">
            <img src="./assets/images/left-image.jpg" alt="Slideshow" />
            <div className="overlay-text">
              <div className="announcement-text">WEDDING ANNOUNCEMENT</div>
              <h1 className="title">TIFFANY & JARED</h1>
              <p className="hashtag">#TImetoshaRE</p>
            </div>
          </div>
          <div className="scroll-to-text" onClick={handleScrollToTextSection}>
            Scroll to Continue
          </div>
        </div>
      )}

      {showTextSection && (
        <div className="text-section">
          <div className="text-content">
            <p>Family & Friends</p>
            <h1>Welcome to Tiffany & Jaredâ€™s Wedding Website</h1>
            <p>
              Together with joyful hearts and the grace of God, we joyfully
              announce the upcoming of our marriage.
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
