import React, { useState, useEffect, useRef } from "react";
import YouTube from "react-youtube";
import "./App.css";
import leftImage from "./assets/images/left-image.jpg";
import rightImage from "./assets/images/right-image.jpg";
import image1 from "./assets/images/image1.jpg";
import image2 from "./assets/images/image2.jpg";
import image3 from "./assets/images/image3.jpg";

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isWelcomeOpen, setIsWelcomeOpen] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0); // Index untuk slider
  const rightSectionRef = useRef(null);
  const playerRef = useRef(null);

  // Array gambar untuk slider
  const images = [image1, image2, image3];

  // Handler untuk pindah ke gambar sebelumnya
  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  // Handler untuk pindah ke gambar berikutnya
  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Handler untuk membuka welcome message
  const handleOpenClick = () => {
    setIsOpen(true);
    if (playerRef.current) {
      playerRef.current.internalPlayer.playVideo();
    }
  };

  const opts = {
    height: "0",
    width: "0",
    playerVars: {
      autoplay: 1,
      controls: 0,
      modestbranding: 1,
      rel: 0,
      showinfo: 0,
    },
  };

  return (
    <div className="app-container">
      <div className="left-section">
        <div className="announcement-text"><b>WEDDING ANNOUNCEMENT</b></div>
        <h1 className="title">TIFFANY & JARED</h1>
        <p className="quote">
          "Aku ingin mencintaimu dengan sederhana; dengan kata yang tak sempat
          diucapkan kayu kepada api yang menjadikannya abu."
        </p>
      </div>

      {!isOpen && (
        <div className="right-section" ref={rightSectionRef}>
          <div className="announcement-text"><b>WEDDING ANNOUNCEMENT</b></div>
          <h1 className="title">TIFFANY & JARED</h1>
          <p className="hashtag">#TImetoshaRE</p>
          <button className="open-button" onClick={handleOpenClick}>
            Open
          </button>
        </div>
      )}

      {isOpen && !isWelcomeOpen && (
        <div className="music-container">
          <YouTube videoId="u03oQwQVR9E" opts={opts} ref={playerRef} />
          <div className="slideshow">
            <img src={images[currentImageIndex]} alt="Slideshow" />
            <div className="overlay-text">
              <h1 className="title">TIFFANY & JARED</h1>
            </div>
          </div>
          <div className="scroll-to-begin" onClick={() => setIsWelcomeOpen(true)}>
            Scroll to Begin
          </div>
        </div>
      )}

      {isWelcomeOpen && (
        <div className="welcome-message">
          <p className="welcome-text">
            DEAR MR-MRS-MS,<br />
            Family & Friends<br />
            Welcome to<br />
            Tiffany & Jared’s Wedding Website<br />
            Together with joyful hearts and the grace of God, we joyfully announce the upcoming of our marriage.
          </p>
          <div className="slider-container">
            <button
              className="slider-button left"
              onClick={handlePrevImage}
              aria-label="Previous image"
            >
              ❮
            </button>
            <img
              src={images[currentImageIndex]}
              alt="Wedding"
              className="slider-image"
            />
            <button
              className="slider-button right"
              onClick={handleNextImage}
              aria-label="Next image"
            >
              ❯
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
